import {
  FETCH_SUCCESS,
  FETCH_START,
} from "../actions";

const initialState = {
  friends: [],
  activeFriend: null
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case FETCH_START:
      return {
        ...state,
      };
    case FETCH_SUCCESS:
      return {
        ...state,
        friends: action.payload
      };
    case ADD_FRIEND:
    const newFriend = {
      name: action.payload.name,
      age: action.payload.age,
      email: action.payload.email
    }
    return {
      ...state,
      friends: [...state.friends, newFriend]
    };
    default:
      return state;
  }
};

export default reducer;
